			["customText"] = "function()\n    if WeakAuras.IsOptionsOpen() then\n        return \"Burst Example:\\n\"..WA_ClassColorName(\"player\")..\" - \"..15\n    else\n        aura_env.last2 = aura_env.last2 or 0\n        aura_env.result = aura_env.result or \"\"\n        if GetTime() >= aura_env.last2+0.5 then\n            aura_env.last2 = GetTime()\n            aura_env.result = \"\"\n            aura_env.debuffs = {}\n            for i=1, #aura_env.bursts do\n                local unit = aura_env.bursts[i]\n                if WA_GetUnitDebuff(unit, aura_env.spellID) then\n                    local expire = select(6, WA_GetUnitDebuff(unit, aura_env.spellID))\n                    local duration = Round(expire-GetTime())\n                    aura_env.debuffs[i] = {UnitName(unit), duration, true}\n                else\n                    aura_env.debuffs[i] = {UnitName(unit), 0, false}\n                end\n            end\n            \n            for i=1, #aura_env.debuffs do\n                if WA_GetUnitDebuff(aura_env.debuffs[i][1], aura_env.spellID) then\n                    if UnitIsUnit(\"player\", aura_env.debuffs[i][1]) then\n                        \n                        aura_env.result = \"|cFFFF0000GET DISPEL|cFFFFFFFF\\n\"\n                        aura_env.last = aura_env.last or 0\n                        if not aura_env.sound then\n                            if IsAddOnLoaded(\"SharedMedia_Causese\") then\n                                PlaySoundFile(\"Interface\\\\AddOns\\\\SharedMedia_Causese\\\\sound\\\\Dispell.ogg\", \"MASTER\")\n                            else\n                                PlaySoundFile(\"Interface\\\\AddOns\\\\WeakAuras\\\\PowerAurasMedia\\\\Sounds\\\\Phone.ogg\", \"MASTER\")\n                            end\n                            aura_env.sound = true\n                            \n                        end\n                        if GetTime() >= aura_env.last+1.5 then\n                            SendChatMessage(\"NEXT DISPEL\", \"SAY\")\n                            aura_env.last = GetTime()\n                        end\n                    end\n                    break\n                end\n            end\n            \n            for i=1,#aura_env.debuffs do\n                if aura_env.debuffs[i][3] then\n                    aura_env.result = aura_env.result..aura_env.text[i]..WA_ClassColorName(aura_env.debuffs[i][1])..\" - \"..aura_env.debuffs[i][2]..\"\\n\"\n                else\n                    aura_env.result = aura_env.result..\"|cFF808080\"..aura_env.debuffs[i][1]..\"|cFFFFFFFF\\n\"\n                end\n            end\n            \n        end\n        \n        return aura_env.result \n    end\nend\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n",
