						["custom"] = "function()\n    if aura_env.unit or aura_env.encounterID == 2101 then\n        if aura_env.dungeons[aura_env.npcID] or aura_env.npcID == 155433 then\n            local aura_env = aura_env\n            local unit = aura_env.unit\n            if not unit then unit = \"boss1\" end\n            aura_env.damagePhysical = 0\n            aura_env.damageMagic = 0\n            local multiplier, voidstacks = 1, 1\n            \n            -- Check buffs on the relevant unit\n            local n = 1\n            local _,_,stacks,_,_,_,_,_,_,buffID,_,_,_,_,_,tooltip1,tooltip2 = UnitBuff(unit, n)\n            while buffID do\n                if buffID == 206150 then -- Mythic+ damage multiplier\n                    multiplier = multiplier * (1 + tooltip2 / 100)\n                elseif buffID == 209859 then -- Bolster Affix\n                    multiplier = multiplier * 1.2\n                elseif buffID == 228318 then -- Enrage Affix\n                    multiplier = multiplier * 2\n                elseif buffID == 259975 then -- Tol Dagor, The Sand Queen - Enrage\n                    multiplier = multiplier * (1.05 * stacks)\n                end\n                n = n + 1\n                _,_,stacks,_,_,_,_,_,_,buffID,_,_,_,_,_,tooltip1,tooltip2 = UnitBuff(unit, n)\n            end\n            \n            n = 1\n            local _,_,stacks,_,_,_,_,_,_,buffID = UnitDebuff(\"player\", n)\n            while buffID do\n                if buffID == 268309 then -- Shrine of the Storm, Deepsea Ritualist - Unending Darkness\n                    multiplier = multiplier * (stacks * 1.25)\n                elseif buffID == 302420 then -- Queen's Decree: Hide\n                    voidstacks = 1 + (stacks / 2)\n                end\n                n = n + 1\n                _,_,stacks,_,_,_,_,_,_,buffID = UnitDebuff(\"player\", n)\n            end\n            \n            if aura_env.npcID == 155433 then -- Void-Touched Emissary\n                aura_env.damageMagic = (aura_env.maxHealth / 2) * voidstacks\n                aura_env.spellNameMagic = \"Hide\"\n                aura_env.aoeMagic = true\n            else\n                if aura_env.encounterID == 2101 then -- Workaround for The Sand Queen, because she becomes untargetable sometimes\n                    aura_env.npcID = 127479\n                end\n                aura_env.damagePhysical = (aura_env.dungeons[aura_env.npcID][1][1] or 0) * multiplier\n                aura_env.spellNamePhysical = aura_env.dungeons[aura_env.npcID][1][2] or \"\"\n                aura_env.aoePhysical = aura_env.dungeons[aura_env.npcID][1][3] or false\n                aura_env.ignoreArmor = aura_env.dungeons[aura_env.npcID][1][4] or false\n                \n                aura_env.damageMagic = (aura_env.dungeons[aura_env.npcID][2][1] or 0) * multiplier\n                aura_env.spellNameMagic = aura_env.dungeons[aura_env.npcID][2][2] or \"\"\n                aura_env.aoeMagic = aura_env.dungeons[aura_env.npcID][2][3] or false\n            end\n            \n            if aura_env.damagePhysical > 0 or aura_env.damageMagic > 0 then\n                return true\n            end\n        end\n    end\nend",
