      frame.string = frame:CreateFontString(nil, \"BACKGROUND\", nil, -7)\n        aura_env.texturePool[aura_env.textureIndex] = frame\n    end\n    return aura_env.texturePool[aura_env.textureIndex]\nend\n\naura_env.Add_cleave_token = function(unit,r,g,b,a)\n    local nameplate = C_NamePlate.GetNamePlateForUnit(unit)\n    if not nameplate then return end\n    local x=1\n    local GUID = UnitGUID(unit)\n    local hit_check = not aura_env.config.confirmation_mode or \n    (aura_env.stored[GUID] and aura_env.main_hit-aura_env.stored[GUID]<0.5)\n    if hit_check then x=1 end\n    local frame = aura_env.getTextureFromPool()\n    frame:ClearAllPoints()\n    frame:SetPoint(\"CENTER\", nameplate, anchor_points[aura_env.config.nameplate_anchor], aura_env.config.anchor_x, aura_env.config.anchor_y)\n    frame:SetSize(aura_env.config.token_size*x, aura_env.config.token_size*x)\n    if hit_check then\n        frame.icon:SetVertexColor(r, g, b, a)\n    else\n        frame.icon:SetVertexColor(0.8,0.8,0.8,0.75)\n    end    \n    frame.icon:SetTexture(anchor_icons[aura_env.config.anchor_icon])    \n    frame:Show()\nend\n\naura_env.Clear_tokens = function()\n    for i=1, #aura_env.texturePool do\n        aura_env.texturePool[i]:Hide()\n    end\n    aura_env.textureIndex = 0    \nend",
