		["Effective Health - Magic and Physical"] = {
			["outline"] = "OUTLINE",
			["color"] = {
				1, -- [1]
				1, -- [2]
				1, -- [3]
				1, -- [4]
			},
			["displayText"] = "%c",
			["customText"] = "function()\n    if aura_env.update or not aura_env.dynamicUpdates then\n        local aura_env = aura_env\n        aura_env.update = false\n        aura_env.frame = 0\n        \n        if aura_env.outOfDate then\n            aura_env.effectiveHealth = \"|cFFFF0000Effective health\\nis out of date|r\"\n            return aura_env.effectiveHealth\n        end\n        \n        local current = {}\n        \n        current.reductionPhysical = aura_env.avoidance * aura_env.masteryReduction *\n        aura_env.statsPhysical * aura_env.buffPhysical * aura_env.talentPhysical *\n        aura_env.healthBasedReduction * aura_env.targetReductionPhysical *\n        aura_env.staggerPhysical * aura_env.lastDefender * aura_env.armor *\n        aura_env.debuffPhysical\n        \n        current.reductionMagic = aura_env.avoidance * aura_env.masteryReduction *\n        aura_env.statsMagic * aura_env.buffMagic * aura_env.talentMagic *\n        aura_env.healthBasedReduction * aura_env.targetReductionMagic *\n        aura_env.staggerMagic * aura_env.lastDefender * aura_env.debuffMagic\n        \n        local removeAvoidance = aura_env.avoidance * aura_env.feint\n        if not aura_env.aoe then\n            current.reductionPhysical = current.reductionPhysical / aura_env.avoidance\n            current.reductionMagic = current.reductionMagic / aura_env.avoidance\n            removeAvoidance = 1\n            if aura_env.elusiveness then\n                removeAvoidance = 1 * aura_env.feint\n            end\n        end\n        \n        current.absorbPhysical = aura_env.absorb +\n        aura_env.necropolisAbsorb + aura_env.touchOfKarma +\n        aura_env.ruggedTenacity + aura_env.aegisOfTheDeep +\n        aura_env.brambles + aura_env.block - aura_env.antiMagicShell -\n        aura_env.guard - aura_env.sanctum - aura_env.nullBarrier -\n        aura_env.edictSeaWitch - aura_env.protectingBitBand\n        \n        current.absorbMagic = aura_env.absorb +\n        aura_env.necropolisAbsorb + aura_env.touchOfKarma +\n        aura_env.ruggedTenacity + aura_env.aegisOfTheDeep +\n        aura_env.brambles - aura_env.bloodShield - aura_env.guard -\n        aura_env.edictMyrmidon\n        \n        current.health = aura_env.currentHealth\n        if aura_env.cheatDeath and aura_env.countCheat then\n            current.health = aura_env.maxHealth * 2\n        end\n        \n        -- Only continue if there are changes\n        local changed = false\n        current.maxHealth = aura_env.maxHealth\n        current.immunityPhysical, current.immunityMagic = aura_env.immunityPhysical, aura_env.immunityMagic\n        current.avoidance = aura_env.avoidance\n        for k, v in pairs(current) do\n            if aura_env.lastUpdate[k] ~= v then\n                changed = true\n                break\n            end\n        end\n        if changed or aura_env.lastUpdateFrame >= 1000 then\n            aura_env.lastUpdate = current\n            aura_env.lastUpdateFrame = 0\n            \n            local EHP, EHM\n            if aura_env.show == 3 then -- If show percentage\n                EHP = (current.health + current.absorbPhysical) / current.reductionPhysical / aura_env.maxHealth * 100\n                EHM = (current.health + current.absorbMagic) / current.reductionMagic / aura_env.maxHealth *   100\n            else\n                EHP = (current.health + current.absorbPhysical) / current.reductionPhysical\n                EHM = (current.health + current.absorbMagic) / current.reductionMagic\n            end\n            \n            -- Format numbers --\n            local charPhysical, divPhysical, decimalPhysical = aura_env.shorten(EHP)\n            local charMagic, divMagic, decimalMagic = aura_env.shorten(EHM)\n            \n            local percent = \"%%\"\n            if aura_env.show == 1 then -- Show effective health\n                EHP = EHP / divPhysical\n                EHM = EHM / divMagic\n                percent = \"\"\n            elseif aura_env.show == 2 then -- Show damage reduction\n                EHP = (1 - current.reductionPhysical) * 100\n                EHM = (1 - current.reductionMagic) * 100\n                if aura_env.decimals == 1 then\n                    decimalPhysical, decimalMagic = 1, 1\n                end\n            end\n            \n            if aura_env.decimals > 1 and aura_env.decimals < 7 then\n                decimalPhysical = aura_env.decimals - 2\n                decimalMagic = aura_env.decimals - 2\n            elseif aura_env.show == 3 then\n                decimalPhysical = 0\n                decimalMagic = 0\n            end\n            \n            -- Format text --\n            local colorPhysical, colorMagic = \"\", \"\"\n            for i, v in ipairs(aura_env.color) do\n                if aura_env.show == 1 and EHP * divPhysical >= aura_env.threshold[1][i] then\n                    colorPhysical = v\n                elseif aura_env.show == 2 then\n                    colorPhysical = v\n                elseif aura_env.show == 3 and EHP * aura_env.maxHealth / 100 >= aura_env.threshold[1][i] then\n                    colorPhysical = v\n                end\n                if aura_env.show == 1 and EHM * divMagic >= aura_env.threshold[2][i] then\n                    colorMagic = v\n                elseif aura_env.show == 2 then\n                    colorMagic = v\n                elseif aura_env.show == 3 and EHM * aura_env.maxHealth / 100 >= aura_env.threshold[2][i] then\n                    colorMagic = v\n                end\n            end\n            if aura_env.immunityPhysical then\n                colorPhysical = aura_env.immunityColor\n            end\n            if aura_env.immunityMagic then\n                colorMagic = aura_env.immunityColor\n            end\n            \n            EHP = string.format(aura_env.textFormatPhysical..decimalPhysical..\"f\"..charPhysical..percent..\"|r\", colorPhysical, EHP)\n            EHM = string.format(aura_env.textFormatMagic..decimalMagic..\"f\"..charMagic..percent..\"|r\", colorMagic, EHM)\n            \n            if aura_env.display == 2 then -- Physical only\n                EHM = \"\"\n            elseif aura_env.display == 3 then -- Magic only\n                EHP = \"\"\n            elseif aura_env.display == 4 then -- None\n                EHP, EHM = \"\", \"\"\n            end\n            \n            if aura_env.debug then\n                aura_env.effectiveHealth = \"P: \"..current.reductionPhysical..\"\\n\"..\"M: \"..current.reductionMagic\n            else\n                aura_env.effectiveHealth = EHP..aura_env.separator..EHM\n            end\n            \n            -- Removing some conditional reductions for export to keep it consistent regardless of user settings\n            local reductionPhysical, reductionMagic = current.reductionPhysical, current.reductionMagic\n            local avoidance = aura_env.avoidance\n            if aura_env.feint < 1 then avoidance = avoidance * 0.60 end\n            if aura_env.conditionalReductions and aura_env.dampenHarm then\n                reductionPhysical = reductionPhysical / 0.50 * 0.80\n                reductionMagic = reductionMagic / 0.50 * 0.80\n            end\n            -- The reductions have avoidance removed and sent on its own\n            WeakAuras.ScanEvents(\"WA_ZELER_EH\",\n                aura_env.currentHealth,\n                current.maxHealth,\n                reductionPhysical / removeAvoidance / aura_env.earthwarden / aura_env.chaoticEnergies,\n                reductionMagic / removeAvoidance / aura_env.chaoticEnergies,\n                current.absorbPhysical - aura_env.block - aura_env.necropolisAbsorb,\n                current.absorbMagic - aura_env.necropolisAbsorb,\n                aura_env.armor,\n                avoidance,\n                aura_env.staggerPhysical,\n                aura_env.staggerMagic,\n                aura_env.immunityPhysical,\n                aura_env.immunityMagic,\n                aura_env.conditionalReductions\n            )\n            \n            if aura_env.debug then\n                print(GetTime(), \"updated\")\n            end\n            return aura_env.effectiveHealth\n        end\n    end\n    return aura_env.effectiveHealth\nend",
