						["custom"] = "-- Original code from https://wago.io/B1M5OqeFb\nfunction(event, ...)\n    if aura_env.classID == 10 and aura_env.specID == 3 then\n        local _, message = ...\n        local destName = select(9, ...)\n        if destName and destName == WeakAuras.me then\n            if message == \"SPELL_ABSORBED\" then\n                local chk = select(12, ...)\n                local spellID, absorbed = 0, 0\n                if type(chk) == \"number\" then\n                    spellID = select(19, ...)\n                    absorbed = select(22, ...)\n                else\n                    spellID = select(16, ...)\n                    absorbed = select(19, ...)\n                end\n                if spellID == 122470 then\n                    aura_env.touchOfKarma = math.max(aura_env.touchOfKarma - absorbed, 0)\n                    aura_env.update = true\n                    return true\n                end\n            elseif message == \"SPELL_AURA_APPLIED\" then\n                local spellID = select(12, ...)\n                if spellID == 122470 then\n                    aura_env.maxValue = aura_env.maxHealth * 0.5\n                    aura_env.touchOfKarma = aura_env.maxValue\n                    aura_env.update = true\n                    return true\n                end\n            elseif message == \"SPELL_AURA_REMOVED\" then\n                local spellID = select(12, ...)\n                if spellID == 122470 then\n                    aura_env.touchOfKarma = 0\n                    aura_env.update = true\n                    return true\n                end\n            end\n        end\n    end\nend",
