		["Effective Health - Magic and Physical v2.23"] = {
			["outline"] = "OUTLINE",
			["xOffset"] = 0,
			["displayText"] = "%c",
			["customText"] = "function()\n    local now = GetTime()\n    if now - aura_env.LastUpdate >= aura_env.Throttle or not aura_env.DynamicUpdates then\n        aura_env.LastUpdate = now\n        if aura_env.OutOfDate then\n            aura_env.EffectiveHealth = \"|cFFFF0000EH is out of date|r\"\n            return aura_env.EffectiveHealth\n        end\n        \n        local aura_env = aura_env\n        \n        -- Armor calulation // Armor scales differently depending on the enemy level, this makes sure is stays accurate\n        local Armor = select(2, UnitArmor(\"player\"))\n        Armor = 1 - C_PaperDollInfo.GetArmorEffectiveness(Armor, aura_env.TargetLevel) -- Gets the reduction percentage and makes it usable\n        \n        \n        -- All Classes --\n        local CurrentHealth = UnitHealth(\"player\")\n        local MaxHealth = UnitHealthMax(\"player\")\n        local Vers = 1 - (GetCombatRatingBonus(31) / 100)\n        local Avoid = 1\n        local CheatDeath = false -- Will be set to true if the player gains a buff to survive fatal damage. The actual Cheat Death talent is ignored\n        local ScanTarget = false\n        if aura_env.AoE then\n            Avoid = 1 - (GetAvoidance() / 100)\n        end\n        local Elusive = 1\n        local StaggerP, StaggerM = 1, 1\n        local Absorbs = UnitGetTotalAbsorbs(\"player\")\n        if aura_env.HighmountainTauren then\n            local Stamina = UnitStat(\"player\", 3)\n            local RuggedTenacity = floor((Stamina * 0.0003 * 20) + 0.5)\n            Absorbs = Absorbs + RuggedTenacity\n        end\n        local AbsorbsP, AbsorbsM = Absorbs, Absorbs\n        \n        -- Initialize some variables --\n        local ClassDRP, ClassDRM = 1, 1\n        local PImmunity, MImmunity = false, false\n        \n        ----- General damage reduction buffs -----\n        if aura_env.UpdateBuffs or not aura_env.DynamicUpdates then -- Check buffs only when there are changes\n            aura_env.UpdateBuffs = false\n            aura_env.BuffFeint = false\n            aura_env.DampenHarm = false\n            aura_env.AMS, aura_env.BloodS = 0, 0\n            aura_env.Guard = 0\n            aura_env.Sanctum = 0\n            local n = 1\n            local _,_,Stacks,_,_,_,Caster,_,_,BuffID,_,_,_,_,_,Tooltip1,Tooltip2,Tooltip3 = UnitBuff(\"player\", n)\n            while BuffID do\n                local Reduction = 1\n                local PhysicalReduction = 1\n                local MagicReduction = 1\n                \n                if aura_env.BasicReductions[BuffID] then\n                    ClassDRP = ClassDRP * aura_env.BasicReductions[BuffID][1]\n                    ClassDRM = ClassDRM * aura_env.BasicReductions[BuffID][2]\n                end\n                \n                \n                ---- More complex tracking ----\n                \n                --- Death Knight ---\n                if BuffID == 48792 then -- Icebound Fortitude // using plus since the tooltip returns a negative value\n                    Reduction = 1 + (tonumber(Tooltip3 or 0)) / 100\n                    \n                elseif BuffID == 48707 then -- Anti-Magic Shell\n                    aura_env.AMS = Tooltip1 or 0\n                    \n                elseif BuffID == 77535 then -- Blood Shield\n                    aura_env.BloodS = Tooltip1 or 0\n                    \n                elseif BuffID == 195181 then -- Bone Shield stacks tracking for exporting\n                    aura_env.BoneShieldStacks = Stacks\n                    \n                    \n                    --- Demon Hunter ---\n                elseif BuffID == 212800 then\n                    if aura_env.DesperateInstincts then\n                        Reduction = 0.50 -- Blur\n                    else\n                        Reduction = 0.65\n                    end\n                    \n                elseif BuffID == 196555 then -- Netherwalk\n                    PImmunity = true\n                    MImmunity = true\n                    \n                    \n                    --- Druid ---\n                elseif BuffID == 203975 and aura_env.ConditionalReductions then -- Earthwarden - Talent\n                    PhysicalReduction = 0.70\n                    \n                    \n                    --- Hunter ---\n                elseif BuffID == 186265 then -- Aspect of the Turtle\n                    -- PImmunity = true\n                    -- MImmunity = true\n                    \n                    \n                    --- Mage ---\n                elseif BuffID == 45438 then -- Ice Block\n                    PImmunity = true\n                    MImmunity = true\n                    \n                    \n                    --- Monk ---\n                elseif BuffID == 261769 then -- Inner Strength // using plus since the tooltip returns a negative value\n                    Reduction = 1 + (Tooltip1 or 0) / 100\n                    \n                elseif BuffID == 115295 then -- Guard // prevents Guard from counting as a normal absorb\n                    aura_env.Guard = Tooltip1\n                    \n                elseif BuffID == 122278 then -- Dampen Harm\n                    aura_env.DampenHarm = true\n                    if aura_env.ConditionalReductions then\n                        Reduction = 0.50\n                    else\n                        Reduction = 0.80\n                    end\n                    \n                    \n                    --- Paladin ---\n                elseif BuffID == 210320 then -- Devotion Aura // using plus since the tooltip returns a negative value\n                    Reduction = 1 + (tonumber(Tooltip1 or 0)) / 100\n                    \n                elseif BuffID == 1022 then -- Blessing of Protection\n                    PImmunity = true\n                    \n                elseif BuffID == 204018 then -- Blessing of Spellwarding\n                    MImmunity = true\n                    \n                elseif BuffID == 188370 then -- Consecration // May not be 100% accurate, but it's good enough for now\n                    Reduction = 1 - (GetMasteryEffect() / 2.85) / 100\n                    \n                elseif BuffID == 31850 then -- Ardent Defender\n                    CheatDeath = true\n                    \n                elseif BuffID == 642 then -- Divine Shield\n                    PImmunity = true\n                    MImmunity = true\n                    \n                    \n                    --- Priest ---\n                elseif BuffID == 45242 then -- Focused Will\n                    if Stacks == 0 then\n                        Reduction = 1 - (1 * 15 / 100)\n                    else\n                        Reduction = 1 - (Stacks * 15 / 100)\n                    end\n                    \n                elseif BuffID == 47788 then -- Guardian Spirit\n                    CheatDeath = true\n                    \n                elseif BuffID == 139 then -- Renew\n                    if aura_env.Perseverance and Caster == \"player\" then -- Perseverance - Talent\n                        Reduction = 0.90\n                    end\n                    \n                elseif BuffID == 274369 then -- Sanctum - Azerite trait\n                    aura_env.Sanctum = Tooltip1 or 0\n                    \n                    \n                    --- Rogue ---\n                elseif BuffID == 1966 then\n                    if not aura_env.AoE and aura_env.Elusiveness then\n                        Elusive = 0.70 -- Elusiveness\n                    else -- Feint\n                        if aura_env.AoE then\n                            aura_env.BuffFeint = true\n                        end\n                    end\n                elseif BuffID == 31224 then -- Cloak of Shadows\n                    MImmunity = true\n                    \n                    \n                    --- Shaman ---\n                elseif BuffID == 260881 then -- Ghost in the Mist - Talent\n                    Reduction = 1 - (tonumber(Stacks or 0)) * 5 / 100\n                    \n                    \n                    --- Warlock ---\n                    \n                    \n                    \n                    --- Warrior ---\n                elseif BuffID == 184362 and aura_env.WarPaint then -- Enrage\n                    Reduction = 0.90 -- War Paint - Talent\n                    \n                    \n                end\n                \n                ClassDRP = ClassDRP * Reduction * PhysicalReduction\n                ClassDRM = ClassDRM * Reduction * MagicReduction\n                n = n + 1\n                _,_,Stacks,_,_,_,Caster,_,_,BuffID,_,_,_,_,_,Tooltip1,Tooltip2,Tooltip3 = UnitBuff(\"player\", n)\n            end\n            aura_env.ClassDRP, aura_env.ClassDRM = ClassDRP, ClassDRM\n            aura_env.PImmunity, aura_env.MImmunity = PImmunity, MImmunity\n            aura_env.CheatDeath = CheatDeath\n            if aura_env.Debug then\n                print(now, \"updated buffs\")\n            end\n        end\n        if aura_env.BuffFeint then\n            Avoid = Avoid * 0.60\n        end\n        AbsorbsP = Absorbs - aura_env.AMS - aura_env.Guard - aura_env.Sanctum\n        AbsorbsM = Absorbs - aura_env.BloodS - aura_env.Guard\n        ClassDRP, ClassDRM = aura_env.ClassDRP, aura_env.ClassDRM\n        PImmunity, MImmunity = aura_env.PImmunity, aura_env.MImmunity\n        CheatDeath = aura_env.CheatDeath\n        \n        ----- Damage reductions with special calculations -----\n        \n        --- Death Knight ---\n        if aura_env.ClassID == 6 then\n            local Necropolis, Necropolis_Absorb = 1, 0\n            if aura_env.Necropolis then -- Will of the Necropolis - Talent\n                if CurrentHealth / MaxHealth < 0.30 then\n                    Necropolis = 0.70\n                elseif aura_env.ConditionalReductions then\n                    -- Necropolis = 1 - (30 / (CurrentHealth / MaxHealth * 100) * 0.30)\n                    Necropolis_Absorb = MaxHealth * 0.30 / 0.70 - MaxHealth * 0.30\n                end\n            end\n            ClassDRP = ClassDRP * Necropolis\n            ClassDRM = ClassDRM * Necropolis\n            AbsorbsP = AbsorbsP + Necropolis_Absorb\n            AbsorbsM = AbsorbsM + Necropolis_Absorb\n        end\n        \n        \n        --- Demon Hunter ---\n        if aura_env.ClassID == 12 then\n            local HavocDemWards, VengDemWards, FieryBrand, VoidReaver = 1, 1, 1, 1\n            if aura_env.SpecID == 1 then\n                HavocDemWards = 0.90 -- Demonic Wards - Passive\n            elseif aura_env.SpecID == 2 then\n                VengDemWards = 0.85 -- Demonic Wards - Passive\n                if select(7, WA_GetUnitDebuff(\"target\", 207744)) == \"player\" then\n                    FieryBrand = 0.60 -- Fiery Brand\n                end\n                if select(7, WA_GetUnitDebuff(\"target\", 268178)) == \"player\" then\n                    VoidReaver = 0.94 -- Void Reaver - Talent\n                end\n            end\n            \n            ClassDRP = ClassDRP * VengDemWards * FieryBrand * VoidReaver\n            ClassDRM = ClassDRM * VengDemWards * FieryBrand * VoidReaver * HavocDemWards\n        end\n        \n        \n        --- Druid ---\n        if aura_env.ClassID == 11 then\n            local GuardA, RendAndTear, Brambles = 1, 1, 0\n            if aura_env.ThickHide then\n                GuardA = 0.94 -- Thick Hide (Guardian Affinity)\n            end\n            if aura_env.SpecID == 3 then\n                if aura_env.RendAndTear then -- Rend and Tear - Talent\n                    local _,_,Stacks,_,_,_,Caster = WA_GetUnitDebuff(\"target\", 192090)\n                    if Caster == \"player\" then\n                        RendAndTear = 1 - 0.02 * (Stacks or 0)\n                    end\n                end\n                if aura_env.Brambles then\n                    -- Brambles = floor(UnitAttackPower(\"player\") * 0.06)\n                    Brambles = floor(UnitAttackPower(\"player\") * 0.067) -- Brambles - Talent\n                end\n            end\n            \n            ClassDRP = ClassDRP * GuardA * RendAndTear\n            ClassDRM = ClassDRM * GuardA * RendAndTear\n            AbsorbsP = AbsorbsP + Brambles\n            AbsorbsM = AbsorbsM + Brambles\n        end\n        \n        \n        --- Hunter ---\n        if aura_env.ClassID == 3 then\n        end\n        \n        \n        --- Mage ---\n        if aura_env.ClassID == 8 then\n        end\n        \n        \n        --- Monk ---\n        if aura_env.ClassID == 10 then\n            local BreathOfFire = 1\n            if aura_env.SpecID == 1 then\n                if select(7, WA_GetUnitDebuff(\"target\", 123725)) == \"player\" then\n                    BreathOfFire = 0.95\n                end\n                -- Get Stagger's effect agaist current target. If no target is found then use base --\n                local StaggerBase, StaggerTarget = C_PaperDollInfo.GetStaggerPercentage(\"player\")\n                if not StaggerTarget then StaggerTarget = StaggerBase end\n                StaggerM = StaggerTarget * 0.35\n                StaggerP = 1 - StaggerTarget / 100\n                StaggerM = 1 - StaggerM / 100\n                \n                -- Stagger cap --\n                local Stagger = UnitStagger(\"player\") or 0 -- Calculate MaxHealth effective reduction for Stagger\n                local StaggerCapP = (CurrentHealth + AbsorbsP) / (CurrentHealth + AbsorbsP + 100 * MaxHealth - Stagger * 10)\n                local StaggerCapM = (CurrentHealth + AbsorbsM) / (CurrentHealth + AbsorbsM + 100 * MaxHealth - Stagger * 10)\n                if StaggerCapP < 0.01 then StaggerCapP = 0.01 end -- Stagger caps at 99%\n                if StaggerCapM < 0.01 then StaggerCapM = 0.01 end\n                if StaggerP < StaggerCapP then StaggerP = StaggerCapP end\n                if StaggerM < StaggerCapM then StaggerM = StaggerCapM end\n            end\n            if aura_env.SpecID == 3 then\n                Absorbs = Absorbs + aura_env.TouchOfKarma\n                AbsorbsP = AbsorbsP + aura_env.TouchOfKarma\n                AbsorbsM = AbsorbsM + aura_env.TouchOfKarma\n            end\n            \n            ClassDRP = ClassDRP * StaggerP * BreathOfFire\n            ClassDRM = ClassDRM * StaggerM * BreathOfFire\n        end\n        \n        \n        --- Paladin ---\n        if aura_env.ClassID == 2 then\n            local LastDefender = 1\n            if aura_env.SpecID == 2 then -- Protection\n                LastDefender = 1 - (1 - 0.97^aura_env.NameplateCounter)\n                if LastDefender < 0.50 then\n                    LastDefender = 0.50\n                end\n            end\n            ClassDRP = ClassDRP * LastDefender\n            ClassDRM = ClassDRM * LastDefender\n        end\n        \n        \n        --- Priest ---\n        if aura_env.ClassID == 5 then\n        end\n        \n        \n        --- Rogue ---\n        if aura_env.ClassID == 4 then\n        end\n        \n        \n        --- Shaman ---\n        if aura_env.ClassID == 7 then\n        end\n        \n        \n        --- Warlock ---\n        if aura_env.ClassID == 9 then\n            local SoulLink = 1\n            local DestructionMastery = 1\n            if aura_env.SpecID == 2 then -- Soul Link passive for Demonology Warlocks\n                if UnitExists(\"pet\") then\n                    SoulLink = 0.80\n                end\n            elseif aura_env.SpecID == 3 then -- Destruction mastery effect\n                DestructionMastery = 1 - ((GetMasteryEffect() / 9) / 100)\n            end\n            \n            ClassDRP = ClassDRP * SoulLink * DestructionMastery\n            ClassDRM = ClassDRM * SoulLink * DestructionMastery\n        end\n        \n        \n        --- Warrior ---\n        if aura_env.ClassID == 1 then\n            local DemoralizingShout, Punish = 1, 1\n            if aura_env.SpecID == 3 then\n                if select(7, WA_GetUnitDebuff(\"target\", 1160)) == \"player\" then -- Demoralizing Shout\n                    DemoralizingShout = 0.80\n                end\n                if select(7, WA_GetUnitDebuff(\"target\", 275335)) == \"player\" then -- Punish\n                    Punish = 0.97\n                end\n            end\n            \n            ClassDRP = ClassDRP * DemoralizingShout * Punish\n            ClassDRM = ClassDRM * DemoralizingShout * Punish\n        end\n        \n        --- Miscellaneous ---\n        if aura_env.DarkIronDwarf then\n            ClassDRP = ClassDRP * 0.99\n        end\n        \n        if aura_env.TreacherousCovenant and CurrentHealth / MaxHealth * 100 < 20 then\n            ClassDRP = ClassDRP * 1.15\n            ClassDRM = ClassDRM * 1.15\n        end\n        \n        if aura_env.AddBlock then\n            local BlockChance = GetBlockChance()\n            local BlockAmount = GetShieldBlock()\n            local Block = 0\n            local CritBlock = 1\n            local Adjust = 1\n            if aura_env.ClassID == 1 then\n                CritBlock = 2\n            end\n            if aura_env.CountBlock == 2 then\n                if BlockChance >= 100 then\n                    Block = BlockAmount\n                end\n            elseif aura_env.CountBlock == 3 then\n                if BlockChance >= 100 then\n                    Block = BlockAmount * CritBlock\n                end\n            elseif aura_env.CountBlock == 4 then\n                Block = BlockAmount\n                Adjust = BlockChance / 100\n                if Adjust > 1 then Adjust = 1 end\n            elseif aura_env.CountBlock == 5 then\n                Block = BlockAmount * 2\n                Adjust = BlockChance / 100\n                if Adjust > 1 then Adjust = 1 end\n            elseif aura_env.CountBlock == 6 then\n                Block = BlockAmount\n            elseif aura_env.CountBlock == 7 then\n                Block = BlockAmount * CritBlock\n            end\n            AbsorbsP = AbsorbsP + (Block * Adjust)\n        end\n        \n        \n        -- Reduction multiplier calculation --\n        local ReductionP = Vers * Avoid * ClassDRP * Armor * Elusive\n        local ReductionM = Vers * Avoid * ClassDRM * Elusive\n        -- Calculate the effective health --\n        if CheatDeath and aura_env.CountCheat then\n            CurrentHealth = MaxHealth * 2\n        end\n        local EHP\n        local EHM\n        if aura_env.UsePercentage then\n            EHP = ((CurrentHealth + AbsorbsP) / ReductionP) / MaxHealth * 100\n            EHM = ((CurrentHealth + AbsorbsM) / ReductionM) / MaxHealth * 100\n        else\n            EHP = (CurrentHealth + AbsorbsP) / ReductionP\n            EHM = (CurrentHealth + AbsorbsM) / ReductionM\n        end\n        \n        \n        -- Export pre-avoidance EHP, EHM, and immunity status as global variables --\n        if aura_env.GlobalExport then\n            -- Calculate maximum effective health without absorbs --\n            local EHPMaxHealth = (MaxHealth + AbsorbsP) / ReductionP\n            local EHMMaxHealth = (MaxHealth + AbsorbsM) / ReductionM\n            -- Export to global variables --\n            local Export = WA_ZELER_EH\n            Export.EHPMAX, Export.EHMMAX = EHPMaxHealth * Avoid * Elusive, EHMMaxHealth * Avoid * Elusive\n            Export.EHP, Export.EHM = EHP * Avoid * Elusive, EHM * Avoid * Elusive\n            \n            Export.REDP, Export.REDM = ReductionP / Avoid / Elusive, ReductionM / Avoid / Elusive\n            Export.ABS, Export.ABSP, Export.ABSM = Absorbs, AbsorbsP, AbsorbsM\n            Export.EHPIMMUNITY, Export.EHMIMMUNITY = PImmunity, MImmunity\n            Export.EHARMOR = Armor\n            Export.STAGGERP, Export.STAGGERM = StaggerP, StaggerM\n            Export.DampenHarm, Export.BuffFeint, Export.BoneShieldStacks = aura_env.DampenHarm, aura_env.BuffFeint, aura_env.BoneShieldStacks\n        end\n        \n        -- Format numbers --\n        local letterP, DivideP, EHPdecimal = aura_env.Shorten(EHP)\n        local letterM, DivideM, EHMdecimal = aura_env.Shorten(EHM)\n        if not aura_env.UsePercentage then\n            EHP = EHP / DivideP\n            EHM = EHM / DivideM\n        end\n        \n        if aura_env.Decimals > 1 then\n            EHPdecimal = aura_env.Decimals - 2\n            EHMdecimal = aura_env.Decimals - 2\n        elseif aura_env.Decimals == 1 and aura_env.UsePercentage then\n            EHPdecimal = 0\n            EHMdecimal = 0\n        end\n        \n        \n        \n        -- Format the displayed text --\n        local Color, Percent = \"\", \"\"\n        if aura_env.UsePercentage then\n            Percent = \"%%\"\n        end\n        if aura_env.Mode ~= 3 then\n            for i = 1, 6 do\n                if EHP >= aura_env.Threshold[1][i] / DivideP and not aura_env.UsePercentage then\n                    Color = aura_env.Color[i]\n                elseif EHP * MaxHealth / 100 >= aura_env.Threshold[1][i] and aura_env.UsePercentage then\n                    Color = aura_env.Color[i]\n                end\n            end\n            if PImmunity then\n                Color = aura_env.Color[7]\n            end\n            EHP = string.format(\"|c\" .. aura_env.Pcolor .. aura_env.TextFormatP .. EHPdecimal .. \"f\" .. letterP .. Percent .. \"|r\", Color, EHP)\n        else EHP = \"\"\n        end\n        \n        if aura_env.Mode ~= 2 then\n            for i = 1, 6 do\n                if EHM >= aura_env.Threshold[2][i] / DivideM and not aura_env.UsePercentage then\n                    Color = aura_env.Color[i]\n                elseif EHM * MaxHealth / 100 >= aura_env.Threshold[2][i] and aura_env.UsePercentage then\n                    Color = aura_env.Color[i]\n                end\n            end\n            if MImmunity then\n                Color = aura_env.Color[7]\n            end\n            EHM = string.format(\"|c\" .. aura_env.Mcolor .. aura_env.TextFormatM .. EHMdecimal .. \"f\" .. letterM .. Percent .. \"|r\", Color, EHM)\n        else EHM = \"\"\n        end\n        \n        local separator = \"\"\n        if aura_env.Mode == 1 and aura_env.Align == 1 then separator = \"\\n\"\n        elseif aura_env.Mode == 1 and aura_env.Align == 2 then separator = \" | \"\n        elseif aura_env.Mode == 1 and aura_env.Align == 3 then separator = \" \"\n        end\n        \n        if aura_env.Debug then\n            aura_env.EffectiveHealth = \"P: \" .. ReductionP .. \"\\n\" .. \"M: \" .. ReductionM\n        else\n            aura_env.EffectiveHealth = EHP .. separator .. EHM\n        end\n        if aura_env.Debug then\n            print(now, \"updated\")\n        end\n    end\n    return aura_env.EffectiveHealth or \"\"\nend",
