				["init"] = {
					["custom"] = "-- Made by Zeler-Ravencrest EU // Discord: Zeler#2221\n\n\n\nlocal Color, Space, text = {0, 1, 0}, \"\", true\nif aura_env.config then -- Prevents errors for those that don't have Weakauras version 2.10.0 or newer\n    aura_env.Mode = aura_env.config.Mode\n    aura_env.Align = aura_env.config.Align\n    aura_env.Debug = aura_env.config.Debug\n    text = aura_env.config.Text\n    aura_env.Decimals = aura_env.config.Decimals\n    aura_env.Pcolor = {aura_env.config.Pcolor[1] * 255, aura_env.config.Pcolor[2] * 255, aura_env.config.Pcolor[3] * 255}\n    aura_env.Mcolor = {aura_env.config.Mcolor[1] * 255, aura_env.config.Mcolor[2] * 255, aura_env.config.Mcolor[3] * 255}\n    aura_env.DynamicUpdates = aura_env.config.DynamicUpdates\n    aura_env.Throttle = aura_env.config.Throttle\n    Color = aura_env.config.Color\n    Space = aura_env.config.Space\n    aura_env.CountCheat = aura_env.config.CountCheat -- Count the cheat death from Guardian Spirit and Ardent Defender as effective health\n    aura_env.AoE = aura_env.config.AoE -- Add Avoidance and Feint into the calculation if set to true\n    aura_env.DynamicColors = aura_env.config.DynamicColors\n    aura_env.CountBlock = aura_env.config.CountBlock\n    aura_env.UsePercentage = aura_env.config.UsePercentage\n    aura_env.ConditionalReductions = aura_env.config.ConditionalReductions\n    if Space then Space = \" \" else Space = \"\" end\nelse\n    aura_env.Mode = 1\n    aura_env.Align = 1\n    aura_env.Debug = false\n    aura_env.AoE = true\n    aura_env.CountCheat = true\n    aura_env.Decimals = 1\n    aura_env.Pcolor = {255, 255, 255}\n    aura_env.Mcolor = {255, 255, 255}\n    aura_env.DynamicUpdates = true\n    aura_env.Throttle = 1\n    aura_env.DynamicColor = true\n    aura_env.CountBlock = false\n    aura_env.UsePercentage = false\n    aura_env.ConditionalReductions = false\nend\n\n\naura_env.GlobalExport = true -- Enable export of some variables as global variables for use in other weakauras\naura_env.LastUpdate = 0\naura_env.UpdateBuffs = true\naura_env.ClassDRP, aura_env.ClassDRM = 1, 1\naura_env.PImmunity, aura_env.MImmunity = false, false\naura_env.CheatDeath = false\naura_env.BuffFeint = false\naura_env.DampenHarm = false\naura_env.AMS, aura_env.BloodS = 0, 0\naura_env.Guard = 0\naura_env.Sanctum = 0\n\n\naura_env.PlayerLevel = UnitLevel(\"player\")\naura_env.TargetLevel = aura_env.PlayerLevel\naura_env.TouchOfKarma = 0\naura_env.HighmountainTauren = IsPlayerSpell(255659) -- Rugged Tenacity - Passive\naura_env.DarkIronDwarf = IsPlayerSpell(265224) -- Forged in Flames - Passive\naura_env.NameplateCheck = 0 -- Protection Paladin - Last Defender - talent\naura_env.NameplateCounter = 0 -- Protection Paladin - Last Defender - talent\naura_env.BoneShieldStacks = 0\n\n\nif text then\n    aura_env.TextFormatP = \"P|r:\" .. Space .. \"|c%s%.\"\n    aura_env.TextFormatM = \"M|r:\" .. Space .. \"|c%s%.\"\nelse\n    aura_env.TextFormatP = \"|r|c%s%.\"\n    aura_env.TextFormatM = \"|r|c%s%.\"\nend\n\n\nlocal GameVersion = GetBuildInfo()\nlocal G1 = select(1, strsplit(\".\", GameVersion))\nlocal G2 = select(2, strsplit(\".\", GameVersion))\nif type(G1) == \"string\" then G1 = tonumber(G1) end\nif type(G2) == \"string\" then G2 = tonumber(G2) end\n\nG2 = G2 / 10\nGameVersion = G1 + G2\nif GameVersion > 8.4 then\n    aura_env.OutOfDate = true\n    return\nelseif GameVersion < 8.0 then\n    aura_env.OutOfDate = true\n    return\nend\n\n\n-- Color values, the accepted values are from 0 to 255\n-- The 1st value is for red, 2nd is for green and 3rd is for blue\nif aura_env.DynamicColors and aura_env.PlayerLevel == 120 then\n    aura_env.Color = {\n        {255, 0, 0},  -- Lowest HP\n        {255, 128, 0},\n        {0, 255, 0},\n        {0, 255, 255},\n        {255, 0, 255},\n        {255, 255, 0},  -- Highest HP\n        {0, 117, 255}, -- The color used when an immunity is active\n    }\nelse\n    aura_env.Color = {\n        {Color[1] * 255, Color[2] * 255, Color[3] * 255},  -- Lowest HP\n        {Color[1] * 255, Color[2] * 255, Color[3] * 255},\n        {Color[1] * 255, Color[2] * 255, Color[3] * 255},\n        {Color[1] * 255, Color[2] * 255, Color[3] * 255},\n        {Color[1] * 255, Color[2] * 255, Color[3] * 255},\n        {Color[1] * 255, Color[2] * 255, Color[3] * 255},  -- Highest HP\n        {0, 117, 255}, -- The color used when an immunity is active\n    }\nend\n\n\n-- Thresholds at which the color changes, the values are in raw effective health number (leave the first at 0)\naura_env.Threshold = {\n    {0, 148000, 216000, 320000, 470000, 620000}, -- Physical\n    {0, 112000, 164000, 240000, 354000, 470000}, -- Magic\n}\n\nlocal function RGBtoHex(rgb)\n    return string.format(\"ff%02x%02x%02x\", rgb[1], rgb[2], rgb[3])\nend\n\nfor i = 1, #aura_env.Color do\n    aura_env.Color[i] = RGBtoHex(aura_env.Color[i])\nend\n\naura_env.Pcolor = RGBtoHex(aura_env.Pcolor)\naura_env.Mcolor = RGBtoHex(aura_env.Mcolor)\n\n-- Check player class and spec\naura_env.ClassID = select(3, UnitClass(\"player\"))\naura_env.SpecID = GetSpecialization()\n\n-- Add or ignore block\naura_env.AddBlock = false\nif aura_env.CountBlock > 1 then\n    if aura_env.ClassID == 1 and aura_env.SpecID == 3 then\n        aura_env.AddBlock = true\n    elseif aura_env.ClassID == 2 and aura_env.SpecID == 2 then\n        aura_env.AddBlock = true\n    end\nend\n\n\n-- Shorten numbers\nfunction aura_env.Shorten(number)\n    local char, divide, decimal = \"\", 1, 1\n    if number >= 10^9 then\n        divide = 10^9\n        char = \"b\"\n        decimal = 2\n    elseif number >= 10^8 then\n        divide = 10^6\n        char = \"m\"\n        decimal = 0\n    elseif number >= 10^7 then\n        divide = 10^6\n        char = \"m\"\n        decimal = 1\n    elseif number >= 10^6 then\n        divide = 10^6\n        char = \"m\"\n        decimal = 2\n    elseif number >= 10^5 then\n        divide = 10^3\n        char = \"k\"\n        decimal = 0\n    elseif number >= 10^4 then\n        divide = 10^3\n        char = \"k\"\n        decimal = 1\n    elseif number >= 10^3 then\n        divide = 10^3\n        char = \"k\"\n        decimal = 2\n    else\n        divide = 1\n        decimal = 0\n    end\n    return char, divide, decimal\nend\n\n\n-- Basic damage reduction calculation. SpellID, physical, and magic reduction\naura_env.BasicReductions = {\n\n    --- Death Knight ---\n    [194679] = {0.70, 0.70}, -- Rune Tap\n\n    --- Demon Hunter ---\n\n\n    --- Druid ---\n    [102342] = {0.80, 0.80}, -- Ironbark\n    [158792] = {0.91, 0.91}, -- Pulverize\n    [22812]  = {0.80, 0.80}, -- Barkskin\n    [61336]  = {0.50, 0.50}, -- Survival Instincts\n\n    --- Hunter ---\n    [186265] = {0.70, 0.70}, -- Aspect of the Turtle\n    [264735] = {0.80, 0.80}, -- Survival of the Fittest\n\n    --- Mage ---\n    [113862] = {0.40, 0.40}, -- Greater Invisibility\n    [235450] = {1.00, 0.85}, -- Prismatic Barrier\n\n    --- Monk ---\n    [120954] = {0.80, 0.80}, -- Fortifying Brew (BrM)\n    [243435] = {0.80, 0.80}, -- Fortifying Brew (MW)\n    [122783] = {1.00, 0.40}, -- Diffuse Magic\n    [115176] = {0.40, 0.40}, -- Zen Meditation\n\n    --- Paladin ---\n    [498]    = {0.80, 0.80}, -- Divine Protection\n    [31850]  = {0.80, 0.80}, -- Ardent Defender\n    [205191] = {0.65, 1.00}, -- Eye for an Eye - talent\n    [86659]  = {0.50, 0.50}, -- Guardian of Ancient Kings\n    [212641] = {0.50, 0.50}, -- Guardian of Ancient Kings - Glyph of the Queen\n    [183436] = {0.70, 0.70}, -- Retribution - passive\n    [6940]   = {0.70, 0.70}, -- Blessing of Sacrifice\n    [204335] = {0.80, 0.80}, -- Aegis of Light - talent\n\n    --- Priest ---\n    [47585]  = {0.25, 0.25}, -- Dispersion\n    [193065] = {0.90, 0.90}, -- Masochism - talent\n    [33206]  = {0.60, 0.60}, -- Pain Suppression\n    -- [194384] = {0.97, 0.97}, -- Leniece - talent -- Currently disabled due to being unable to track if the casting priest has the talent or not\n    [81782]  = {0.75, 0.75}, -- Power Word: Barrier\n\n    --- Rogue ---\n    [45182]  = {0.15, 0.15}, -- Cheating Death - Cheat Death talent\n\n    --- Shaman ---\n    [108271] = {0.60, 0.60}, -- Astral Shift\n    [98007]  = {0.90, 0.90}, -- Spirit Link Totem\n    [118337] = {0.60, 0.60}, -- Harden Skin - Primal Elementalist (talent) - Earth Elemental\n\n    --- Warlock ---\n    [104773] = {0.60, 0.60}, -- Unending Resolve\n\n    --- Warrior ---\n    [197690] = {0.80, 0.80}, -- Defensive Stance\n    [118038] = {0.70, 0.70}, -- Die by the Sword\n    [184364] = {0.70, 0.70}, -- Enraged Regeneration\n    [871]    = {0.60, 0.60}, -- Shield Wall\n    [23920]  = {1.00, 0.80}, -- Spell Reflection\n    [223658] = {0.70, 0.70}, -- Safeguard\n\n    --- Miscellaneous ---\n    [241836] = {0.95, 0.95}, -- Shadow of Celumbra\n    [65116]  = {0.90, 1.00}, -- Stoneform\n    [211080] = {0.90, 0.90}, -- Comforting light (Court of Stars)\n    [273809] = {1.30, 1.30}, -- Idol of Rage\n    [268212] = {0.25, 0.25}, -- Minor Reinforcing Ward (Shrine of the Storm)\n    [267904] = {0.25, 0.25}, -- Reinforcing Ward (Shrine of the Storm)\n}\n\n-- Talent check\nfunction aura_env.CheckTalents()\n    local ClassID = aura_env.ClassID\n\n    --- Death Knight ---\n    if ClassID == 6 then\n        aura_env.Necropolis = IsPlayerSpell(206967)\n\n        --- Demon Hunter ---\n    elseif ClassID == 12 then\n        aura_env.DesperateInstincts = IsPlayerSpell(205411)\n\n        --- Druid ---\n    elseif ClassID == 11 then\n        aura_env.ThickHide = IsPlayerSpell(16931)\n        aura_env.RendAndTear = IsPlayerSpell(204053)\n        aura_env.Brambles = IsPlayerSpell(203953)\n\n        --- Hunter ---\n    elseif ClassID == 3 then\n\n\n        --- Mage ---\n    elseif ClassID == 8 then\n\n\n        --- Monk ---\n    elseif ClassID == 10 then\n\n\n        --- Paladin ---\n    elseif ClassID == 2 then\n        aura_env.LastDefender = IsPlayerSpell(203791)\n\n        --- Priest ---\n    elseif ClassID == 5 then\n        aura_env.Perseverance = IsPlayerSpell(235189)\n\n        --- Rogue ---\n    elseif ClassID == 4 then\n        aura_env.Elusiveness = IsPlayerSpell(79008)\n        --- Shaman ---\n    elseif ClassID == 7 then\n\n\n        --- Warlock ---\n    elseif ClassID == 9 then\n\n\n        --- Warrior ---\n    elseif ClassID == 1 then\n        aura_env.WarPaint = IsPlayerSpell(208154)\n\n    end\nend\n\naura_env.CheckTalents()\n\nfunction aura_env.CheckAzeriteTraits()\n    aura_env.TreacherousCovenant = false\n    if aura_env.PlayerLevel == 120 then\n        local AllAzeriteTraits = {}\n        local function GetAzeritePowerIDs(slot)\n            local SpellIDs = {}\n            if GetInventoryItemLink(\"player\", slot) then\n                local ItemLocation = ItemLocation:CreateFromEquipmentSlot(slot)\n                if C_AzeriteEmpoweredItem.IsAzeriteEmpoweredItem(ItemLocation) then\n                    local TierInfo = C_AzeriteEmpoweredItem.GetAllTierInfo(ItemLocation)\n                    for AzeriteTier, TierInfo in pairs(TierInfo) do\n                        for i, idx in pairs(TierInfo.azeritePowerIDs) do\n                            if C_AzeriteEmpoweredItem.IsPowerSelected(ItemLocation, idx) then\n                                local PowerInfo = C_AzeriteEmpoweredItem.GetPowerInfo(idx)\n                                local AzeriteSpellID = PowerInfo[\"spellID\"]\n                                if AzeriteSpellID ~= 263978 then -- Trait that gives 5 ilvls, we don't need to track this\n                                    table.insert(SpellIDs, AzeriteSpellID)\n                                    table.insert(AllAzeriteTraits, AzeriteSpellID)\n                                    if AzeriteSpellID == 288989 or AzeriteSpellID == 288953 then\n                                        aura_env.TreacherousCovenant = true\n                                    end\n                                end\n                            end\n                        end\n                    end\n                end\n                return SpellIDs\n            end\n        end\n        local AzeriteHelm = GetAzeritePowerIDs(1)\n        local AzeriteShoulder = GetAzeritePowerIDs(3)\n        local AzeriteChest = GetAzeritePowerIDs(5)\n        -- aura_env.AllAzeriteTraits = AllAzeriteTraits\n    end\nend\n\naura_env.CheckAzeriteTraits()\n\n\nif aura_env.GlobalExport then\n    WA_ZELER_EH = {}\nend",
