						["genericShowOn"] = "showOnActive",
						["custom"] = "function(event, unit)\n    -- print(event)\n    if event == \"ACTIVE_TALENT_GROUP_CHANGED\" then\n        aura_env.SpecID = GetSpecialization()\n        aura_env.CheckTalents()\n        aura_env.LastUpdate = 0\n        aura_env.AddBlock = false\n        if aura_env.CountBlock > 1 then -- Change block settings if needed\n            if aura_env.ClassID == 1 and aura_env.SpecID == 3 then\n                aura_env.AddBlock = true\n            elseif aura_env.ClassID == 2 and aura_env.SpecID == 2 then\n                aura_env.AddBlock = true\n            end\n        end\n    elseif event == \"PLAYER_DAMAGE_DONE_MODS\" then -- Force update block values\n        if aura_env.ClassID == 1 and aura_env.SpecID == 3 then -- Protection Warrior\n            aura_env.LastUpdate = 0\n        elseif aura_env.ClassID == 2 and aura_env.SpecID == 2 then -- Protection Paladin\n            aura_env.LastUpdate = 0\n        end\n    elseif event == \"UNIT_AURA\" and unit == \"player\" then\n        aura_env.LastUpdate = 0\n        aura_env.UpdateBuffs = true\n    elseif unit == \"player\" or event == \"PLAYER_EQUIPMENT_CHANGED\" then\n        aura_env.LastUpdate = 0\n    elseif unit == \"target\" then -- Update weakaura when unit aura triggers on target for specs that need it\n        if aura_env.ClassID == 12 and aura_env.SpecID == 2 then -- Vengeance Demon Hunter\n            aura_env.LastUpdate = 0\n            aura_env.UpdateBuffs = true\n        elseif aura_env.ClassID == 11 and aura_env.SpecID == 3 then -- Guardian Druid\n            aura_env.LastUpdate = 0\n            aura_env.UpdateBuffs = true\n        elseif aura_env.ClassID == 10 and aura_env.SpecID == 1 then -- Brewmaster Monk\n            aura_env.LastUpdate = 0\n            aura_env.UpdateBuffs = true\n        end\n    end\nend",
