			["displayText"] = "%c",
			["customText"] = "function()\n    -- Import reduction, absorbs, and immunity\n    local Import = WA_ZELER_EH\n    if not Import then return end -- To avoid errors\n    local ReductionP, ReductionM = Import.REDP, Import.REDM\n    local Absorbs, AbsorbsP, AbsorbsM = Import.ABS, Import.ABSP, Import.ABSM\n    local Armor = Import.EHARMOR\n    local StaggerP, StaggerM = Import.STAGGERP, Import.STAGGERM\n    local BuffDampenHarm, BuffFeint, BoneShieldStacks = Import.BuffDampenHarm, Import.BuffFeint, Import.BoneShieldStacks\n    aura_env.PImmunity = Import.EHPIMMUNITY or false\n    aura_env.MImmunity = Import.EHMIMMUNITY or false\n    \n    if not Absorbs then return end -- To avoid errors\n    \n    \n    -- Set some aura_env to local variables to improve performance\n    local ClassID = aura_env.ClassID\n    local SpecID = aura_env.SpecID\n    \n    \n    -- Health checking\n    local CurrentHealth = UnitHealth(\"player\")\n    local MaxHealth = UnitHealthMax(\"player\")\n    local PercentHealth = CurrentHealth / MaxHealth * 100\n    \n    -- Avoidance calculation\n    local AvoidP, AvoidM, ElusiveP, ElusiveM, AvoidAdjustP, AvoidAdjustM = 1, 1, 1, 1, 1, 1\n    if aura_env.PAoE then\n        AvoidP = 1 - (GetAvoidance() / 100)\n        if BuffFeint then AvoidP = AvoidP * 0.60 end -- Feint\n    elseif BuffFeint and aura_env.Elusiveness then   -- Elusiveness\n        ElusiveP = 0.70\n    end\n    if aura_env.MAoE then\n        AvoidM = 1 - (GetAvoidance() / 100)\n        if BuffFeint then AvoidM = AvoidM * 0.60 end -- Feint\n    elseif BuffFeint and aura_env.Elusiveness then   -- Elusiveness\n        ElusiveM = 0.70\n    end\n    \n    if not aura_env.PAoE then\n        AvoidAdjustP = 1 - (GetAvoidance() / 100)\n        if BuffFeint then AvoidAdjustP = (AvoidAdjustP * 0.60 / ElusiveP) end -- Feint\n    end\n    if not aura_env.MAoE then\n        AvoidAdjustM = 1 - (GetAvoidance() / 100)\n        if BuffFeint then AvoidAdjustM = (AvoidAdjustM * 0.60 / ElusiveM) end -- Feint\n    end\n    \n    if aura_env.IgnoreArmor == 1 then\n        ReductionP = ReductionP / Armor\n    end\n    \n    ReductionP = ReductionP * AvoidP * ElusiveP\n    ReductionM = ReductionM * AvoidM * ElusiveM\n    \n    local EHP, EHM = CurrentHealth / (ReductionP * AvoidAdjustP), CurrentHealth / (ReductionM * AvoidAdjustM)\n    \n    -- Get spell damage\n    local DamageSpellName = \"\"\n    local DamageP = aura_env.PSpellDamage or 0\n    local DamageM = aura_env.MSpellDamage or 0\n    \n    if WeakAuras.IsOptionsOpen() then\n        aura_env.MSpellName = \"After Hit\"\n        DamageM = 150000\n    end\n    \n    -- Dampen Harm\n    if ClassID == 10 then\n        if BuffDampenHarm then -- Dampen Harm\n            ReductionP = ReductionP / 0.8 / StaggerP -- Remove reductions from the base module\n            ReductionM = ReductionM / 0.8 / StaggerM\n            -- Reduction scales linearly with the size of the hit relative to max health\n            local HitSizeP = ReductionP * DamageP / MaxHealth\n            local HitSizeM = ReductionM * DamageM / MaxHealth\n            if HitSizeP > 1 then HitSizeP = 1 end\n            if HitSizeM > 1 then HitSizeM = 1 end\n            local DampenHarmP = 0.8 - 0.3 * HitSizeP\n            local DampenHarmM = 0.8 - 0.3 * HitSizeM\n            ReductionP = ReductionP * DampenHarmP * StaggerP\n            ReductionM = ReductionM * DampenHarmM * StaggerM\n        end\n    end\n    \n    \n    if ClassID == 6 then -- Death Knight talent reductions\n        if aura_env.SpectralDeflect then -- Spectral Deflection - talent\n            local HitSizeP = ReductionP * DamageP / MaxHealth * 100\n            local HitSizeM = ReductionM * DamageM / MaxHealth * 100\n            if BoneShieldStacks > 1 and HitSizeP > 25 then\n                ReductionP = ReductionP / 0.84 -- Remove damage reduction from Bone Shield\n                ReductionP = ReductionP * 0.68 -- Add damage reduction from Spectral Deflection\n            end\n            if BoneShieldStacks > 1 and HitSizeM > 25 then\n                ReductionM = ReductionM / 0.84 -- Remove damage reduction from Bone Shield\n                ReductionM = ReductionM * 0.68 -- Add damage reduction from Spectral Deflection\n            end\n        end\n        if aura_env.Necropolis and PercentHealth >= 30 then -- Calculate the damage reduction from WIll of the Necropolis\n            if DamageP > 0 then\n                local abs = AbsorbsP / ReductionP\n                local MaxEH = MaxHealth / ReductionP\n                local damage = DamageP - abs\n                local absorbed = DamageP - damage\n                if (EHM - damage) / MaxEH * 100 < 30 then\n                    local dmgpercent = 30 - ((EHP - damage) / MaxEH * 100)\n                    local expectedreduction = damage - (dmgpercent / 100) * MaxEH * 0.70\n                    expectedreduction = (damage - expectedreduction)\n                    local notreduced = (EHP / MaxEH * 100 - 30)\n                    DamageP = notreduced * MaxEH / 100 + expectedreduction + absorbed\n                end\n            elseif DamageM > 0 then\n                local abs = AbsorbsM / ReductionM\n                local MaxEH = MaxHealth / ReductionM\n                local damage = DamageM - abs\n                local absorbed = DamageM - damage\n                if (EHM - damage) / MaxEH * 100 < 30 then\n                    local dmgpercent = 30 - ((EHM - damage) / MaxEH * 100)\n                    local expectedreduction = damage - (dmgpercent / 100) * MaxEH * 0.70\n                    expectedreduction = (damage - expectedreduction)\n                    local notreduced = (EHM / MaxEH * 100 - 30)\n                    DamageM = notreduced * MaxEH / 100 + expectedreduction + absorbed\n                end\n            end\n        end\n    end\n    \n    \n    -- Calculate the remaining health\n    local HPleft\n    if not aura_env.Percentage then\n        DamageP = (DamageP / AvoidAdjustP) - (Absorbs - AbsorbsM)\n        if DamageP < 0 then DamageP = 0 end\n        DamageM = (DamageM / AvoidAdjustM) - (Absorbs - AbsorbsP)\n        if DamageM < 0 then DamageM = 0 end\n        Absorbs = AbsorbsP + AbsorbsM - Absorbs\n        \n        if DamageP > 0 then HPleft = EHP + (Absorbs / (ReductionP * AvoidAdjustP)) - DamageP - DamageM\n        elseif DamageM > 0 then HPleft = EHM + (Absorbs / (ReductionM * AvoidAdjustM)) - DamageP - DamageM\n        end\n        \n        -- Format the remaining effective health\n        local Divide = 1\n        local Deci = 0\n        if HPleft > 10^9 then\n            HPleft = HPleft / 10^9\n            Divide = 10^9\n            Deci = 2\n        elseif HPleft > 10^8 then\n            HPleft = HPleft / 10^6\n            Divide = 10^6\n        elseif HPleft > 10^7 then\n            HPleft = HPleft / 10^6\n            Divide = 10^6\n            Deci = 1\n        elseif HPleft > 10^6 then\n            HPleft = HPleft / 10^6\n            Divide = 10^6\n            Deci = 2\n        elseif HPleft > 10^5 then\n            HPleft = HPleft / 10^3\n            Divide = 10^3\n        elseif HPleft > 10^4 then\n            HPleft = HPleft / 10^3\n            Divide = 10^3\n            Deci = 1\n        elseif HPleft > 10^3 then\n            HPleft = HPleft / 10^3\n            Divide = 10^3\n            Deci = 2\n        elseif HPleft > -10^3 then\n            HPleft = HPleft / 1\n            Divide = 1\n            Deci = 0\n        elseif HPleft > -10^4 then\n            HPleft = HPleft / 10^3\n            Divide = 10^3\n            Deci = 2\n        elseif HPleft > -10^5 then\n            HPleft = HPleft / 10^3\n            Divide = 10^3\n            Deci = 1\n        elseif HPleft > -10^6 then\n            HPleft = HPleft / 10^3\n            Divide = 10^3\n            Deci = 0\n        elseif HPleft > -10^7 then\n            HPleft = HPleft / 10^6\n            Divide = 10^6\n            Deci = 2\n        elseif HPleft > -10^8 then\n            HPleft = HPleft / 10^6\n            Divide = 10^6\n            Deci = 1\n        elseif HPleft > -10^9 then\n            HPleft = HPleft / 10^6\n            Divide = 10^6\n        elseif HPleft <= -10^9 then\n            HPleft = HPleft / 10^9\n            Divide = 10^9\n            Deci = 2\n        end\n        HPleft = aura_env.Color(HPleft, Divide, Deci)\n        \n    else\n        DamageP = DamageP * ReductionP - (Absorbs - AbsorbsM)\n        if DamageP < 0 then DamageP = 0 end\n        DamageM = DamageM * ReductionM - (Absorbs - AbsorbsP)\n        if DamageM < 0 then DamageM = 0 end\n        Absorbs = AbsorbsP + AbsorbsM - Absorbs\n        HPleft = (CurrentHealth + Absorbs - DamageP - DamageM) / MaxHealth * 100\n        if HPleft > PercentHealth then HPleft = PercentHealth end\n        HPleft = aura_env.Color(HPleft)\n    end\n    \n    -- Format the displayed text\n    if aura_env.PSpellName then\n        DamageSpellName = \"|c\" .. aura_env.PTextColor .. aura_env.PSpellName .. \"|r\"\n    end\n    if aura_env.MSpellName then\n        DamageSpellName = \"|c\" .. aura_env.MTextColor .. aura_env.MSpellName .. \"|r\"\n    end\n    \n    return DamageSpellName .. HPleft\nend",
