						["custom"] = "function()\n    -- Reset to default values\n    aura_env.PSpellDamage = 0\n    aura_env.PSpellName = false\n    aura_env.PAoE = false\n    aura_env.IgnoreArmor = 0\n    \n    aura_env.MSpellDamage = 0\n    aura_env.MSpellName = false\n    aura_env.MAoE = false\n    \n    if aura_env.Debug then\n        aura_env.MSpellDamage = 150000\n        aura_env.MAoE = true\n        aura_env.MSpellName = \"Debug\"\n    end\n    \n    local unit = \"target\"\n    if UnitExists(\"boss1\") then unit = \"boss1\" end\n    local Multiplier = 1\n    \n    -- Prioritize boss if in a boss encounter\n    local unit = \"target\"\n    if UnitExists(\"boss1\") then unit = \"boss1\" end\n    if UnitExists(unit) or aura_env.encounterID == 2101 then\n        -- Check the buffs on the relevant unit\n        local n = 1\n        local _,_,Stacks,_,_,_,_,_,_,BuffID,_,_,_,_,_,HP,Damage = UnitBuff(unit, n)\n        while BuffID do\n            if BuffID == 206150 then -- Mythic+ damage multiplier\n                Multiplier = Multiplier * (1 + Damage / 100)\n            elseif BuffID == 209859 then -- Bolster Affix\n                Multiplier = Multiplier * 1.2\n            elseif BuffID == 228318 then -- Enrage Affix\n                Multiplier = Multiplier * 2\n            elseif BuffID == 259975 then -- Tol Dagor, The Sand Queen - Enrage\n                Multiplier = Multiplier * (1.05 * Stacks)\n            end\n            n = n + 1\n            _,_,Stacks,_,_,_,_,_,_,BuffID,_,_,_,_,_,HP,Damage = UnitBuff(unit, n)\n        end\n        \n        \n        local n = 1\n        local _,_,Stacks,_,_,_,_,_,_,BuffID = UnitDebuff(\"player\", n)\n        while BuffID do\n            if BuffID == 268309 then -- Shrine of the Storm, Deepsea Ritualist - Unending Darkness\n                Multiplier = Multiplier * (Stacks * 1.25)\n            end\n            n = n + 1\n            _,_,Stacks,_,_,_,_,_,_,BuffID = UnitDebuff(\"player\", n)\n        end\n        \n        -- Get npc ID\n        local NpcID = UnitGUID(unit)\n        if aura_env.encounterID == 2101 then -- Workaround for The Sand Queen, because she becomes untargetable sometimes\n            NpcID = 127479\n        else\n            NpcID = select(6, strsplit(\"-\", NpcID))\n            NpcID = tonumber(NpcID)\n        end\n        \n        -- Check physical damage\n        local Dungeon = aura_env.PDungeon\n        if Dungeon[NpcID] then\n            aura_env.PSpellDamage = (Dungeon[NpcID][1] or 0) * Multiplier\n            aura_env.PSpellName = Dungeon[NpcID][2] or false\n            aura_env.PAoE = Dungeon[NpcID][3] or false\n            aura_env.IgnoreArmor = Dungeon[NpcID][4] or 0\n        end\n        \n        -- Check magic damage\n        Dungeon = aura_env.MDungeon\n        if Dungeon[NpcID] then\n            aura_env.MSpellDamage = (Dungeon[NpcID][1] or 0) * Multiplier\n            aura_env.MSpellName = Dungeon[NpcID][2] or false\n            aura_env.MAoE = Dungeon[NpcID][3] or false\n        end\n    end\n    if aura_env.PSpellDamage > 0 or aura_env.MSpellDamage > 0 then\n        -- if aura_env.PImmunity then aura_env.PSpellDamage = 0 end\n        -- if aura_env.MImmunity then aura_env.MSpellDamage = 0 end\n        return true\n    else\n        return false\n    end\n    return false\nend",
